#define DEBUG_MODULE 0

#define IMPORTANCE_NONE		0.0
#define IMPORTANCE_LOW		0.25
#define IMPORTANCE_MED		0.5
#define IMPORTANCE_HIGH		1.0

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

fixed_3d_object_data
	fixed_3d_object_database[OBJECT_3D_LAST];

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

static void set_fixed_3d_object_database_item (object_3d_index_numbers object_index, int explosive_quality, int explosive_power, int hardness, int initial_damage,
																float importance, target_types target_type, target_symbol_types target_symbol)
{
	int
		destroyed_object_index;

	//
	// validate parameters
	//

	ASSERT ((object_index > OBJECT_3D_INVALID_OBJECT_INDEX) && (object_index < OBJECT_3D_LAST));

	ASSERT ((explosive_quality >= EXPLOSIVE_QUALITY_NONE) && (explosive_quality <= EXPLOSIVE_QUALITY_FLAMMABLE));

	ASSERT ((explosive_power >= EXPLOSIVE_POWER_NONE) && (explosive_power <= EXPLOSIVE_POWER_HIGH));

	ASSERT ((hardness == WARHEAD_EFFECTIVE_LIGHT_BUILDING) || (hardness == WARHEAD_EFFECTIVE_STONE_BUILDING) || (hardness == WARHEAD_EFFECTIVE_HARDENED_BUILDING));

	ASSERT ((importance == IMPORTANCE_NONE) || 
				(importance == IMPORTANCE_LOW) || 
				(importance == IMPORTANCE_MED) || 
				(importance == IMPORTANCE_HIGH));

	ASSERT (initial_damage < (1 << 12));

	ASSERT ((target_type == TARGET_TYPE_INVALID) || (target_type == TARGET_TYPE_GROUND));

	ASSERT (target_symbol < NUM_TARGET_SYMBOL_TYPES);

	if (target_type == TARGET_TYPE_INVALID)
	{
		ASSERT (importance == IMPORTANCE_NONE);
	}

	#if DEBUG_MODULE
	
	if (fixed_3d_object_database [object_index].valid_entry)
	{
		debug_filtered_log ("FX_OBJDB: WARNING Object %s already defined", object_3d_information_database [object_index].name);
	}

	#endif

	//
	// set values
	//

	fixed_3d_object_database [object_index].explosive_quality = explosive_quality;

	fixed_3d_object_database [object_index].explosive_power = explosive_power;

	if (hardness == WARHEAD_EFFECTIVE_LIGHT_BUILDING)
	{
		fixed_3d_object_database [object_index].hardness = 0;
	}
	else if (hardness == WARHEAD_EFFECTIVE_STONE_BUILDING)
	{
		fixed_3d_object_database [object_index].hardness = 1;
	}
	else if (hardness == WARHEAD_EFFECTIVE_HARDENED_BUILDING)
	{
		fixed_3d_object_database [object_index].hardness = 2;
	}

	fixed_3d_object_database [object_index].initial_damage = initial_damage;

	fixed_3d_object_database [object_index].importance = importance;

	fixed_3d_object_database [object_index].target_type = target_type;

	fixed_3d_object_database [object_index].target_symbol = target_symbol;

	fixed_3d_object_database [object_index].valid_entry = TRUE;

	//
	// copy values for destroyed versions of objects
	//

	destroyed_object_index = get_3d_object_destroyed_object_index (object_index);

	fixed_3d_object_database [destroyed_object_index].target_type = target_type;

	fixed_3d_object_database [destroyed_object_index].target_symbol = target_symbol;

	fixed_3d_object_database [destroyed_object_index].valid_entry = TRUE;
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

void report_non_entries_in_fixed_3d_object_database (void)
{
	#if DEBUG_MODULE

	int
		loop;

	const char
		*s;

	for (loop = OBJECT_3D_INVALID_OBJECT_INDEX + 1; loop < OBJECT_3D_LAST; loop ++)
	{
		if (!fixed_3d_object_database [loop].valid_entry)
		{
			if (fixed_3d_object_database [loop].keysite_object)
			{
				s = object_3d_information_database [loop].name;
	
				if ((strstr (s, "DESTROYED")) || (strstr (s, "BRIDGE")))
				{
					continue;
				}
	
				debug_filtered_log ("FX_OBJDB : No Entry for %s", s);
			}
		}
	}

	#endif
}

